import React from 'react';
import {
    View,
    Text,
    Button,
    TouchableOpacity,
    Dimensions,
    TextInput,
    Platform,
    StyleSheet,
    ScrollView,
    StatusBar,
    SafeAreaView,
    Keyboard,
    RefreshControl
} from 'react-native';

import * as Animatable from 'react-native-animatable';
import { LinearGradient } from 'expo-linear-gradient'
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import Feather from 'react-native-vector-icons/Feather';
import { BorderlessButton } from 'react-native-gesture-handler';

const Otp = ({ navigation }) => {
  componentDidMount=()=>{
      RefreshControl.nameref.focus()
  }

    return (


        <View style={styles.container}>

            <StatusBar backgroundColor='#4700b3' barStyle="light-content" />

            
            <View style={styles.header} />
            <Animatable.View
                animation="fadeInUp"
                style={styles.footer}
            >
                <Text style={{
                fontFamily: 'nunito-bold',
                fontSize: 28,
                color: '#4700b3',
                
               
                
            }}>Verification Code</Text>
                <Text style={{
                    fontFamily: 'nunito-semi',
                    fontSize: 15,
                    paddingBottom: 30,
                    color: 'grey',

                }}>Enter the OTP code sent to your email</Text>

                <View style={styles.containerInput}>
                    <View >
                        <TextInput 
                            keyboardType="numeric" 
                            maxLength={1} 
                            style={styles.cellView}
                            style={styles.cellInput} 
                        ></TextInput>
                    </View>
                    <View style={styles.cellView}>
                        <TextInput keyboardType="numeric" maxLength={1}
                        style={styles.cellInput}
                        ></TextInput>
                    </View>
                    <View style={styles.cellView}>
                        <TextInput keyboardType="numeric" maxLength={1}
                        style={styles.cellInput}
                        ></TextInput>
                    </View>
                    <View style={styles.cellView}>
                    <TextInput keyboardType="numeric" maxLength={1}
                    style={styles.cellInput}
                    ></TextInput>
                    </View>
                </View>
                <View style={styles.button}>
                    <TouchableOpacity
                        style={styles.signIn}
                        onPress={() => { }}
                    >
                        <LinearGradient
                            colors={['#4700b3', '#4700b3']}
                            style={styles.signIn}
                        >
                            <Text style={[styles.textSign, {
                                color: '#fff',
                            }]}>Verify</Text>
                        </LinearGradient>
                    </TouchableOpacity>
                </View>

                <View style={styles.textPrivate}>
                    <Text style={[{ fontFamily: 'nunito-semi' }, { color: 'grey' }]}>
                        Didn't get code?
                </Text>
                    <TouchableOpacity>
                        <Text style={[{ fontFamily: 'nunito-bold' }, { color: '#4700b3' }]}>{" "}Resend OTP</Text>
                    </TouchableOpacity>

                </View>

            </Animatable.View>

        </View>


    );
};

export default Otp;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#4700b3'
    },

    header: {
            flex: 1,
            justifyContent: 'flex-end',
            paddingHorizontal: 20,
            paddingBottom: 50
        },
    footer: {
        flex: 2,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 30,
        paddingVertical: 30
    },
    text_footer: {
        color: '#05375a',
        fontSize: 18,

    },
    action: {
        flexDirection: 'row',
        marginTop: 15,
        borderBottomColor: '#4700b3',
        borderBottomWidth: 2,
    },
    button: {
        alignItems: 'center',
        marginTop: 50,
        paddingBottom: 10,
    },
    signIn: {
        width: '100%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 30,
        elevation: 7,
    },
    textSign: {
        fontFamily: 'nunito-bold',
        fontSize: 20,
    },
    textPrivate: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        marginTop: 20
    },
    color_textPrivate: {
        color: 'grey'
    },
    containerInput: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-evenly'
    },
    cellView: {
        paddingVertical: 11,
        width: 40,
        margin: 5,
        justifyContent: 'center',
        alignItems: 'center',
        borderBottomWidth: 2,
        borderBottomColor: '#4700b3'
    },
    cellInput: {
        textAlign: "center",
        fontSize: 25,
        fontFamily: 'nunito-semi',


    }
});
