import React from 'react';
import {
    View,
    Text,
    Button,
    TouchableOpacity,
    Dimensions,
    TextInput,
    Platform,
    StyleSheet,
    ScrollView,
    StatusBar,
    SafeAreaView
} from 'react-native';

import * as Animatable from 'react-native-animatable';
import { LinearGradient } from 'expo-linear-gradient'
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import Feather from 'react-native-vector-icons/Feather';
import { BorderlessButton } from 'react-native-gesture-handler';
import axios from 'axios'

const Daily = ({ navigation }) => {

    return (


        <View style={styles.container}>
            <ScrollView contentContainerStyle={{ flexGrow: 1, justifyContent: 'center' }}>
                <StatusBar backgroundColor='#fff' barStyle="dark-content" />
                <View style={styles.header}>
                    <Text style={{
                        fontFamily: 'nunito-semi',
                        fontSize: 25,
                        color: '#000000',
                        paddingTop: 10,
                        textAlign: 'center'
                    }}>Daily Survey</Text>
                    <Text style={{
                        fontFamily: 'nunito-bold',
                        fontSize: 15,
                        color: '#000000',
                    }}>Take your daily Servey</Text>
                </View>

                <View style={styles.button}>
                    <TouchableOpacity style={styles.popUp} >
                        <LinearGradient colors={['#fff', '#fff']} style={styles.popUp}>

                            <Text style={[styles.textSign, {
                                color: 'black',
                            }]}>Daily Servey</Text>
                        </LinearGradient>
                    </TouchableOpacity>
                </View>
                <View style={styles.button}>
                    <TouchableOpacity style={styles.popUp} >
                        <LinearGradient colors={['#fff', '#fff']} style={styles.popUp}>

                            <Text style={[styles.textSign, {
                                color: 'black',
                            }]}>Weekly Servey</Text>
                        </LinearGradient>
                    </TouchableOpacity>
                </View>
                <View style={styles.button}>
                    <TouchableOpacity style={styles.popUp} >
                        <LinearGradient colors={['#fff', '#fff']} style={styles.popUp}>

                            <Text style={[styles.textSign, {
                                color: 'black',
                            }]}>Montly Servey</Text>
                        </LinearGradient>
                    </TouchableOpacity>
                </View>
            </ScrollView>
        </View>
    );
};

export default Daily;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff'
    },
    header: {
        flex: 0.1,
        paddingHorizontal: 20,
    },
    footer: {
        flex: 1,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 30,
        paddingVertical: 30
    },
    text_footer: {
        color: '#05375a',
        fontSize: 18,

    },
    action: {
        flexDirection: 'row',
        marginTop: 15,

        borderBottomColor: '#b380ff',
        borderBottomWidth: 2,
    },
    textInput: {
        flex: 1,

        fontFamily: 'nunito-regular',
        color: '#000000',

    },
    button: {
        alignItems: 'center',
        marginTop: 10,
    },
    popUp: {
        width: '95%',
        height: 100,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 20,

    },
    textSign: {
        fontFamily: 'nunito-bold',
        fontSize: 20,
    },
    textPrivate: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        marginTop: 20
    },
    color_textPrivate: {
        color: 'grey'
    },
});
