import React from 'react';
import { 
    View, 
    Text, 
    Button, 
    TouchableOpacity, 
    Dimensions,
    TextInput,
    Platform,
    StyleSheet,
    ScrollView,
    StatusBar,
    SafeAreaView
} from 'react-native';

import * as Animatable from 'react-native-animatable';
import { LinearGradient } from 'expo-linear-gradient'
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import Feather from 'react-native-vector-icons/Feather';
import { BorderlessButton } from 'react-native-gesture-handler';

const Otp = ({navigation}) => {


    return (
      
            
      <View style={styles.container}>
           
        <StatusBar backgroundColor='#4700b3' barStyle="light-content"/>
       
        <Text style={{
                fontFamily: 'nunito-bold',
                fontSize: 28,
                color: '#fff',
                textAlign: 'center',
                paddingTop: 30,
            }}>Verification Code</Text>
               <View style={styles.header} />
        <Animatable.View 
            animation="fadeInUp"
            style={styles.footer}
        >  
            <Text style={{
                fontFamily: 'nunito-semi',
                fontSize: 18,
                paddingBottom: 30,
                color: 'grey',

            }}>Please enter OTP code sent to your email</Text>
            
                <View style={styles.textPrivate}>
                <Text style={[{fontFamily: 'nunito-semi'},{color: 'grey'}]}>
                    Didn't get code?
                </Text>
                <TouchableOpacity>
                <Text style={[{fontFamily: 'nunito-bold'},{color:'#4700b3'}]}>{" "}Resend OTP</Text>
                </TouchableOpacity>  
           
            </View>   
               
      </Animatable.View>   
      
      </View> 
     
      
    );
};

export default Otp;

const styles = StyleSheet.create({
    container: {
      flex: 1, 
      backgroundColor: '#4700b3'
    },
    
    safeArea: {
        flex: 1,
        marginTop: StatusBar.currentHeight + 10,
        paddingHorizontal: '3%', 
        backgroundColor: '#4700b3'
      },
    header: {
        flex: 1,
        justifyContent: 'flex-end',
        paddingHorizontal: 20,
        paddingBottom: 50
    },
    footer: {
        flex: 14,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 30,
        paddingVertical: 30
    },
    text_footer: {
        color: '#05375a',
        fontSize: 18,
        
    },
    action: {
        flexDirection: 'row',
        marginTop: 15,
       
        borderBottomColor: '#4700b3',
        borderBottomWidth: 2,
    },
    textInput: {
        flex: 1,
        
        fontFamily: 'nunito-regular',
        color: '#000000',
        
    },
    button: {
        alignItems: 'center',
        marginTop: 50, 
        paddingBottom:10,    
    },
    signIn: {
        width: '100%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 30,
        elevation: 7,
    },
    textSign: {
        fontFamily: 'nunito-bold',
        fontSize: 20,
    },
    textPrivate: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        marginTop: 20
    },
    color_textPrivate: {
        color: 'grey'
    },
  });
