import React from 'react'
import {
    View,
    Text,
    StyleSheet,
    TextInput,
    Platform,
    Button,
    StatusBar,
    Image,
    TouchableOpacity,
} from 'react-native'
import Feather from 'react-native-vector-icons/Feather'
import * as Animatable from 'react-native-animatable'
import { LinearGradient } from 'expo-linear-gradient'
import MaterialIcons from 'react-native-vector-icons/MaterialIcons'



const SignUp = () => {
    const [data, setData] = React.useState({
        email: '',
        password: '',
        check_textInputChange: false,
        secureTextEntry: true
    })
    const textInputChange = (val) => {
        if (val.length > 10) {
            setData({
                ...data,
                email: val,
                check_textInputChange: true
            })
        }
        else {
            setData({
                ...data,
                email: val,
                check_textInputChange: false
            })
        }
    }
    const handlePasswordChange = (val) => {
        setData({
            ...data,
            password: val
        })
    }
    const updateSecuredTextEntry = () => {
        setData({
            ...data,
            secureTextEntry: !data.secureTextEntry
        })
    }

    return (
        <View style={styles.container}>
            <StatusBar backgroundColor='#20b2aa' barStyle="light-content" />
            <View style={styles.header}>
            </View>

            <Animatable.View
                animation='fadeInUp'
                style={styles.footer}
            >
                <Text style={styles.text_footer}>Hey, get on board</Text>
                <Text style={styles.text_footer2}>Sign up to continue!</Text>

                <View style={styles.action}>
                    <TextInput
                        placeholder="Email Address"
                        style={styles.textInput}
                        autoCapitalize='none'
                        onChangeText={(val) => textInputChange(val)}
                    />
                    {data.check_textInputChange ?
                        <Feather
                            name="check"
                            size={20}
                            color="cyan"
                        />
                        : null}
                </View>
                <View style={styles.action}>
                    <TextInput
                        placeholder="Password"
                        style={styles.textInput}
                        autoCapitalize='none'
                        secureTextEntry={data.secureTextEntry ? true : false}
                        onChangeText={(val) => handlePasswordChange(val)}
                    />
                    <TouchableOpacity
                        onPress={updateSecuredTextEntry}
                    >
                        {data.secureTextEntry ?
                            <Feather
                                name="eye-off"
                                color="cyan"
                                size={20}
                            />
                            :
                            <Feather
                                name="eye"
                                color="red"
                                size={20}
                            />
                        }
                    </TouchableOpacity>
                </View>

                <View style={styles.action}>
                    <TextInput
                        placeholder="Confirm Password"
                        style={styles.textInput}
                        autoCapitalize='none'
                        secureTextEntry={data.secureTextEntry ? true : false}
                        onChangeText={(val) => handlePasswordChange(val)}
                    />
                    <TouchableOpacity
                        onPress={updateSecuredTextEntry}
                    >
                        {data.secureTextEntry ?
                            <Feather
                                name="eye-off"
                                color="cyan"
                                size={20}
                            />
                            :
                            <Feather
                                name="eye"
                                color="red"
                                size={20}
                            />
                        }
                    </TouchableOpacity>
                </View>
                <View style={styles.button}>
                    <LinearGradient
                        colors={['#20b2aa', '#20b2aa']}
                        style={styles.signUp}
                    >
                        <Text style={[styles.textSignUp, {
                            color: '#fff'
                        }]}>SIGN UP</Text>
                    </LinearGradient>
                </View>
                <Text style={{ 
                    paddingTop: 50,
                    textAlign: 'center',
                    color: '#778899'
                    }}>Or Login with</Text>
                    
            </Animatable.View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#20b2aa',
    },
    header: {
        flex: 1,
        justifyContent: 'flex-end',
        paddingHorizontal: 20,
        paddingBottom: 50
    },
    footer: {
        flex: 3,
        
        backgroundColor: '#f8f8ff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 20,
        paddingVertical: 30
    },
    text_header: {
        color: '#fff',
        fontWeight: 'bold',
        fontSize: 30,
        textAlign: 'center',
    },
    text_footer: {
        fontSize: 25,
        color: '#20b2aa',
        paddingHorizontal: 20,
        fontWeight: 'bold'
    },
    text_footer2: {
        fontSize: 15,
        paddingHorizontal: 20,
        paddingBottom: 10,
        color: '#778899',
    },
    text_footer3: {
        fontSize: 15,
        paddingHorizontal: 20,
        color: '#778899',
        paddingTop: 20
    },
    textInput: {
        flex: 1,
        color: '#000000',
        borderBottomColor: '#66cdaa',
        borderBottomWidth: 2,
    },
    action: {
        flexDirection: 'row',
        paddingHorizontal: 20,
        paddingTop: 10
    },
    button: {
        alignItems: 'center',
        marginTop: 50,
        paddingBottom: 10,
    },
    signUp: {
        width: '80%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 30,
    },
    textSignUp: {
        fontSize: 15,
        fontWeight: 'bold',
    },

})


export default SignUp
